import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Button } from "react-bootstrap";
import Line from "../../components/core/Line/Line";
import axios from 'axios';
import { createPost } from "./model/effects";
import { sessionSelect } from "../../redux/core/session/selectors";
import './PostCreateWidget.css';
import "@uploadthing/react/styles.css";

const PostCreateWidget: React.FC = () => {
    const userId = useSelector(sessionSelect.userId);
    const [description, setDescription] = useState<string>('');
    const [file, setFile] = useState<File | null>(null);

    const datePosted = new Date().toISOString().split('T')[0];
    const dispatch = useDispatch();

    const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => setDescription(event.target.value);
    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFile(event.target.files ? event.target.files[0] : null);
    };

    const uploadFile = async (file: File) => {
        if (!file) return;

        const options = {
            method: 'POST',
            url: 'https://uploadthing.com/api/uploadFiles',
            headers: {
                'Content-Type': 'application/json',
                'X-Uploadthing-Api-Key': 'sk_live_031e6fada3707a21afcd57f8ae2d4a7e02f273eff16721784c2c3e52cc522564',
                'X-Uploadthing-Version': '6.9.0'
            },
            data: {
                files: [{
                    name: file.name+ userId,
                    size: file.size,
                    type: file.type,
                    customId: userId,
                    acl: "public-read",
                    contentDisposition: "upload"
                }],
                metadata: null,
            }
        };

        try {
            const { data } = await axios.request(options);
            console.log(data);
            return data;
        } catch (error) {
            console.error('Upload Error:', error);
            throw error;
        }
    };

    const handleSubmit = async () => {
        if (file) {
            try {

                const uploadResult = await uploadFile(file);
                console.log(uploadResult);

                if (uploadResult && uploadResult.url) {
                    await createPost({
                        userId, photo: uploadResult.url, datePosted, description, dispatch
                    });
                }
            } catch (error) {
                console.error('Upload Error:', error);
            }
        }
    };

    return (
        <div className='widget-main-create'>
            <div>
                <div>
                    <input
                        placeholder="Description"
                        type='text'
                        onChange={handleDescriptionChange}/>
                    <input
                        type="file"
                        onChange={handleFileChange}/>
                </div>
                <Button onClick={handleSubmit}>Create Post</Button>
                <Line/>
            </div>
        </div>
    )
}

export default PostCreateWidget;
