import React from 'react';
import {useDispatch, useSelector} from 'react-redux';
import Button from '../../components/core/Button/Button';
import { logoutUser } from '../auth-login-widget/model/effects'; // Adjust the import path as necessary
import "./SettingsMainWidget.css";
import {sessionSelect} from "../../redux/core/session/selectors";
import {logout} from "../auth-login-widget/model/reducers";

const LogoutButton: React.FC = () => {
    const dispatch = useDispatch();
    const userId = useSelector(sessionSelect.userId);

    const handleLogout1 = () => {
        dispatch(logout());
        // Use window.location.assign to navigate and refresh
       // window.location.assign('/login');
    };
    const handleLogout = () => {
        logoutUser({ userId, dispatch }).then(() => {
            handleLogout1();
            window.location.assign('/login');
        });
    };

    return (
        <div className="log-out-container">
            <Button content="Log Out" onClick={handleLogout} />
        </div>
    );
};

export default LogoutButton;
