// SimpleModal.tsx

import React from 'react';
import "./SimpleModal.css";

interface SimpleModalProps {
    isOpen: boolean;
    handleClose: () => void;
    handlePrev: () => void; // Add this prop
    handleNext: () => void; // Add this prop
    children: React.ReactNode;
}

const SimpleModal: React.FC<SimpleModalProps> = ({ isOpen, handleClose, handlePrev, handleNext, children }) => {
    if (!isOpen) {
        return null;
    }

    return (
        <div className="modal-overlay" onClick={handleClose}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                {children}
                <button className="modal-nav-button prev" onClick={handlePrev}>Prev</button>
                <button className="modal-nav-button next" onClick={handleNext}>Next</button>
            </div>
        </div>
    );
};

export default SimpleModal;
