// StorySlider.js

import React, { useState } from "react";
import { StoryType } from "../../../types/auth";
import Story from "../../core/Story/Story";
import SimpleModal from "./SimpleModal";

interface StorySliderProps {
    stories: StoryType[];
}

const StorySlider: React.FC<StorySliderProps> = ({ stories }) => {
    const [selectedStoryIndex, setSelectedStoryIndex] = useState<number | null>(null);

    const handleStoryClick = (index: number) => {
        setSelectedStoryIndex(index);
    };

    const handleCloseModal = () => {
        setSelectedStoryIndex(null);
    };

    const handlePrevStory = () => {
        setSelectedStoryIndex((prevIndex) =>
            prevIndex !== null && prevIndex > 0 ? prevIndex - 1 : prevIndex
        );
    };

    const handleNextStory = () => {
        setSelectedStoryIndex((prevIndex) =>
            prevIndex !== null && prevIndex < stories.length - 1 ? prevIndex + 1 : prevIndex
        );
    };

    return (
        <>
            <div style={{
                width: '100%',
                display: 'flex',
                flexDirection: 'row',
                left: 0,
                gap: 10,
                padding: 10
            }}>
                {stories.map((story, index) => (
                    <div onClick={() => handleStoryClick(index)} key={story.storyId}>
                        <Story {...story} />
                    </div>
                ))}
            </div>
            {selectedStoryIndex !== null && (
                <SimpleModal isOpen={selectedStoryIndex !== null} handleClose={handleCloseModal}>
                    <div className="modal-content" onClick={e => e.stopPropagation()}>
                        {children}
                        <button className="modal-nav-button prev" onClick={handlePrev}>Prev</button>
                        <button className="modal-nav-button next" onClick={handleNext}>Next</button>
                    </div>
                </SimpleModal>
            )}
        </>
    );
};

export default StorySlider;
