import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {Button} from "react-bootstrap";
import LText from "../../components/core/LText/LText";
import Line from "../../components/core/Line/Line";
import BText from "../../components/core/BText/BText";
import {createPost} from "./model/effects";
import {sessionSelect} from "../../redux/core/session/selectors";

const PostCreateWidget: React.FC = () => {

    const jwtToken = useSelector(sessionSelect.jwtToken);
    const userId = useSelector(sessionSelect.userId);
    const [photo, setPhoto] = useState<string>('');
    const [datePosted, setDatePosted] = useState<string>('');
    const [description, setDescription] = useState<string>('');


    const dispatch = useDispatch();

    const handlePhotoChange = (event: React.ChangeEvent<HTMLInputElement>) => setPhoto(event.target.value);
    const handleDatePostedChange = (event: React.ChangeEvent<HTMLInputElement>) => setDatePosted(event.target.value);
    const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => setDescription(event.target.value);
    const handleSubmit = async () => {
        await createPost({
            userId, photo, datePosted, description, dispatch
        });
    }

    return (
        <>
        <div className="post-back">
            <>
            <div className="post-page-container">
                <p className="post-title">Create a New Post</p>
            </div>
            <div className="post-container">
                <p className="post-logo">YourLogo</p>
                <div className="post-forms">
                    <input
                        className="post-form"
                        placeholder="Photo URL"
                        type='text'
                        onChange={handlePhotoChange}/>
                    <input
                        className="post-form"
                        placeholder="Date Posted"
                        type='date'
                        onChange={handleDatePostedChange}/>
                    <input
                        className="post-form"
                        placeholder="Description"
                        type='text'
                        onChange={handleDescriptionChange}/>
                </div>
                <Button content='Create Post' onClick={handleSubmit}/>
                {/*{errorMessage && <LText text={errorMessage} color={'#ff0000'}/>}*/}
                <Line/>
                <div className="post-under">
                    <LText text="View posts?"/>
                    <Link to='/posts' className='link-btn'>
                        <BText text="Posts" />
                    </Link>
                </div>
            </div>
            </>
        </div>
        </>
    )
}

export default PostCreateWidget;
