// StorySlider.js

import React, { useState } from "react";
import { StoryType } from "../../../types/auth";
import Story from "../../core/Story/Story";
import SimpleModal from "./SimpleModal";

interface StorySliderProps {
    stories: StoryType[];
}

const StorySlider: React.FC<StorySliderProps> = ({ stories }) => {
    const [selectedStory, setSelectedStory] = useState<StoryType | null>(null);

    const handleStoryClick = (story) => {
        setSelectedStory(story);
    };

    const handleCloseModal = () => {
        setSelectedStory(null);
    };

    return (
        <>
            <div style={{
                width: '100%',
                display: 'flex',
                flexDirection: 'row',
                left: 0,
                gap: 10,
                padding: 10
            }}>
                {stories.map(story => (
                    <div onClick={() => handleStoryClick(story)} key={story.storyId}>
                        <Story {...story} />
                    </div>
                ))}
            </div>
            <SimpleModal isOpen={!!selectedStory} handleClose={handleCloseModal}>
                <img src={selectedStory?.photo} alt="Story" />
            </SimpleModal>
        </>
    );
};

export default StorySlider;
