import React, {useEffect} from "react";
import BText from "../../core/BText/BText";
import LText from "../../core/LText/LText";
import './styles.css';
import {PRIMARY_LIGHT} from "../../../utils/constants";
import Button from "../../core/Button/Button";
import {useSelector} from "react-redux";
import {profileSelect} from "../../../widgets/profile-overview-widget/model/selectors";
import {dataRequested, followUnfollow} from "../../../widgets/profile-overview-widget/model/effects";
import {closeSidebar, showSidebar} from "../../../redux/core/layout/reducers";
const connection = useSelector(profileSelect.profileConnection);
interface SuggestedFriendProps {
    name: string;
    profilePicture: string;
    commonFollower: string;
}
useEffect(() => {
    dataRequested({userId: profileUserId, myUserId: userId, jwtToken, dispatch});
}, [jwtToken, profileUserId])

const SuggestedFriend: React.FC<SuggestedFriendProps> = ({
                                            name,
                                            profilePicture,
                                            commonFollower
                                        }) => {
    return (
        <div className="suggested-friend-card">
            <div className="suggested-friend-text-img">
                <img src={profilePicture} className="suggested-friend-photo"/>
                <div className="suggested-friend-text">
                    <BText text={name}/>
                    <LText text={"Followed by " + commonFollower + " + more"}/>
                </div>
            </div>
            <div className="suggested-friend-follow">
                <Button content={connection} onClick={() => {
                        followUnfollow({userId: profileUserId, myUserId: "", jwtToken, dispatch})
                    }
                }/>
            </div>
        </div>
    )
}

export default SuggestedFriend;
