import React from "react";
import BText from "../../core/BText/BText";
import LText from "../../core/LText/LText";
import './styles.css';
import {PRIMARY_LIGHT} from "../../../utils/constants";
import {useDispatch, useSelector} from "react-redux";
import {feedSelect} from "../../../widgets/feed-main-widget/model/selectors";
import {followUnfollow} from "../../../widgets/profile-overview-widget/model/effects";
import {profileSelect} from "../../../widgets/profile-overview-widget/model/selectors";
import {sessionSelect} from "../../../redux/core/session/selectors";

interface SuggestedFriendProps {
    name: string;
    profilePicture: string;
    commonFollower: string;
    refreshSuggestedFriends: () => void;
}


const SuggestedFriend: React.FC<SuggestedFriendProps> = ({
                                            name,
                                            profilePicture,
                                            commonFollower,
                                            refreshSuggestedFriends
                                        }) => {

    const dispatch = useDispatch();
    const jwtToken = useSelector(sessionSelect.jwtToken);

    const profileUserId =  useSelector(sessionSelect.userId);
    const suggestedFriends = useSelector(feedSelect.suggestedFriends);
    return (
        <div className="suggested-friend-card">
            <div className="suggested-friend-text-img">
                <img src={profilePicture} className="suggested-friend-photo"/>
                <div className="suggested-friend-text">
                    <BText text={name}/>
                    <LText text={"Followed by " + commonFollower + " + more"}/>
                </div>
            </div>
            <div className="suggested-friend-follow">
                <BText text='Follow' color={PRIMARY_LIGHT} onClick={()=>
                {
                    // @ts-ignore
                    console.log("userId "+ profileUserId+ " myUserId "+suggestedFriends.at(0).userId)
                    // @ts-ignore
                    followUnfollow({userId: suggestedFriends.at(0).userId, myUserId: profileUserId, jwtToken, dispatch})
                }}/>
            </div>
        </div>
    )
}

export default SuggestedFriend;
