import React from 'react';
import "./SimpleModal.css";

interface SimpleModalProps {
    isOpen: boolean;
    handleClose: () => void;
    handlePrev: () => void;
    handleNext: () => void;
    username: string;
    profilePicture: string;
    children: React.ReactNode;
}

const SimpleModal: React.FC<SimpleModalProps> = ({
                                                     isOpen, handleClose, handlePrev, handleNext, username,
                                                     profilePicture, children
                                                 }) => {
    if (!isOpen) {
        return null;
    }

    return (
        <div className="modal-overlay" onClick={handleClose}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                <div className="modal-header">
                    <img src={profilePicture} alt={username} className="modal-profile-picture"/>
                    <span className="modal-username">{username}</span>
                </div>
                {children}
                <button className="modal-nav-button prev" onClick={handlePrev}></button>
                <button className="modal-nav-button next" onClick={handleNext}></button>
            </div>
        </div>
    );
};

export default SimpleModal;
