import React from "react";

interface MessageBubbleProps {
    content: string;
    isMine: boolean;
    firstCorner: boolean;
    secondCorner: boolean;
}

const MessageBubble: React.FC<MessageBubbleProps> = ({
                                                         content,
                                                         isMine,
                                                         firstCorner,
                                                         secondCorner}) => {
    let cornersStyle = '';
    if(firstCorner && secondCorner)
        cornersStyle = 'both-corners';
    else if(firstCorner && isMine)
        cornersStyle = 'third-corner';
    else if(secondCorner && isMine)
        cornersStyle = 'second-corner';
    else if(firstCorner && !isMine)
        cornersStyle = 'forth-corner';
    else if(secondCorner && !isMine)
        cornersStyle = 'first-corner';

    const positionStyle = isMine ? 'my-message' : 'your-message';

    // Updated function to determine if content is an image URL
    const isImageUrl = (url: string) => {
        // Adjusting regex to account for parameters after the image file extension
        return /^(https?:\/\/.*\.(?:png|jpg|jpeg|gif|bmp)(?:\?.*)?)$/i.test(url);
    };

    // Conditional rendering based on whether content is an image URL
    const renderContent = () => {
        if (isImageUrl(content)) {
            return <img src={content} alt="Sent content" style={{ maxWidth: '30%', height: 'auto' }} />;
        } else {
            return <p>{content}</p>;
        }
    };

    return (
        <div className={'message-bubble ' + cornersStyle + ' ' + positionStyle}>
            {renderContent()}
        </div>
    );
}

export default MessageBubble;
