import React from 'react';
import './PostModal.css';

const PostModal = ({ post, handleClose }) => {
    if (!post) {
        return null;
    }

    return (
        <div className="post-modal-overlay" onClick={handleClose}>
            <div className="post-modal-content" onClick={(e) => e.stopPropagation()}>
                <img src={post.photo} alt="Post" className="post-modal-image" />
                <button onClick={handleClose} className="post-modal-close">Close</button>
            </div>
        </div>
    );
};

export default PostModal;
