import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Button } from "react-bootstrap";
import Line from "../../components/core/Line/Line";
import { createPost } from "./model/effects";
import { sessionSelect } from "../../redux/core/session/selectors";
import { useFilePicker, useUpload } from "@uploadthing/react";
import './PostCreateWidget.css';

const PostCreateWidget: React.FC = () => {
    const userId = useSelector(sessionSelect.userId);
    const [description, setDescription] = useState<string>('');

    const datePosted = new Date().toISOString().split('T')[0]; // Formats the date as 'YYYY-MM-DD'
    const dispatch = useDispatch();

    // Uploadthing integration
    const { openFilePicker } = useFilePicker();
    const { upload } = useUpload({
        appId: "f8s67x9u8k",
        secret: "sk_live_031e6fada3707a21afcd57f8ae2d4a7e02f273eff16721784c2c3e52cc522564"
    });

    const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => setDescription(event.target.value);

    const handleUpload = async () => {
        const files = await openFilePicker();
        if (files.length > 0) {
            const result = await upload(files[0]);
            if (result.success) {
                handleSubmit(result.url);
            } else {
                console.error('Failed to upload file:', result.error);
            }
        }
    };

    const handleSubmit = async (photo: string) => {
        await createPost({
            userId, photo, datePosted, description, dispatch
        });
    }

    return (
        <div className='widget-main-create'>
            <div>
                <div>
                    <input
                        placeholder="Description"
                        type='text'
                        onChange={handleDescriptionChange}/>
                </div>
                <Button onClick={handleUpload}>Upload Photo/Video</Button>
                <Button onClick={() => handleSubmit("")}>Create Post</Button>
                <Line/>
            </div>
        </div>
    )
}

export default PostCreateWidget;
