import React, { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Button } from "react-bootstrap";
import Line from "../../components/core/Line/Line";
import { createPost } from "./model/effects";
import { sessionSelect } from "../../redux/core/session/selectors";
import './PostCreateWidget.css';
import { storage } from "../../firebase";
import { ref, uploadBytes, getDownloadURL } from '@firebase/storage';
import { v4 as uuidv4 } from 'uuid';

const PostCreateWidget: React.FC = () => {
    const userId = useSelector(sessionSelect.userId);
    const [description, setDescription] = useState<string>('');
    const [file, setFile] = useState<File | null>(null);
    const [preview, setPreview] = useState<string | null>(null);
    const fileInputRef = useRef<HTMLInputElement>(null);
    const datePosted = new Date().toISOString().split('T')[0];
    const dispatch = useDispatch();

    const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setDescription(event.target.value);
    };

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const files = event.target.files;
        if (files && files[0]) {
            setFile(files[0]);
            setPreview(URL.createObjectURL(files[0]));
        }
    };

    const handleClick = () => {
        fileInputRef.current?.click();
    };

    const uploadImage = async () => {
        if (file) {
            const imageRef = ref(storage, `images/${userId}_${file.name}_${uuidv4()}`);
            try {
                const snapshot = await uploadBytes(imageRef, file);
                return await getDownloadURL(snapshot.ref);
            } catch (error) {
                alert("Failed to upload image: " + error);
                throw error;  // This stops the post creation if the upload fails
            }
        }
        throw new Error("No file selected");
    };

    const handleSubmit = async () => {
        try {
            if (file) {
                const photoURL = await uploadImage();
                await createPost({
                    userId, photo: photoURL, datePosted, description, dispatch
                });
                alert("Post created successfully!");
            } else {
                alert("Please select a file to upload.");
            }
        } catch (error) {
            console.error("Failed to create post:", error);
            alert(error);
        }
    };

    return (
        <div className='widget-main-create'>
                <div className='image-upload-container' onClick={handleClick}>
                    {preview ? (
                        <img src={preview} alt="preview" className="image-preview" />
                    ) : (
                        <div className="upload-placeholder">Upload Image</div>
                    )}
                    <input
                        ref={fileInputRef}
                        type="file"
                        onChange={handleFileChange}
                        style={{ display: 'none' }}/>
                </div>
                <div>
                    <input
                        className="description-input"
                        placeholder="Description"
                        type='text'
                        onChange={handleDescriptionChange}/>
                </div>
                <Button className="create-post-button" onClick={handleSubmit}>Create Post</Button>
                <Line/>
        </div>
    );
}

export default PostCreateWidget;
