import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {Button} from "react-bootstrap";
import Line from "../../components/core/Line/Line";
import {createPost} from "./model/effects";
import {sessionSelect} from "../../redux/core/session/selectors";
import './PostCreateWidget.css';

const PostCreateWidget: React.FC = () => {
    const userId = useSelector(sessionSelect.userId);
    const [photo, setPhoto] = useState<string>('');
    const [description, setDescription] = useState<string>('');

    // Set the date to the current date
    const datePosted = new Date().toISOString().split('T')[0]; // Formats the date as 'YYYY-MM-DD'

    const dispatch = useDispatch();

    const handlePhotoChange = (event: React.ChangeEvent<HTMLInputElement>) => setPhoto(event.target.value);
    const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => setDescription(event.target.value);
    const handleSubmit = async () => {
        await createPost({
            userId, photo, datePosted, description, dispatch
        });
    }

    return (
        <div className='widget-main-create'>
            <div>
                <div>
                    <input
                        placeholder="Photo URL"
                        type='text'
                        onChange={handlePhotoChange}/>
                    <input
                        placeholder="Description"
                        type='text'
                        onChange={handleDescriptionChange}/>
                </div>
                <Button onClick={handleSubmit}>Create Post</Button>
                <Line/>
            </div>
        </div>
    )
}

export default PostCreateWidget;
