import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { CompatClient, Stomp } from '@stomp/stompjs';
import {messageSelect} from "../../../widgets/messaging-overview-widget/model/selectors";
import {sessionSelect} from "../../../redux/core/session/selectors";
import { updateMessages } from '../../../redux/actions';

import Phone from '../../../assets/icons/phone.svg';
import Video from '../../../assets/icons/video.svg';
import Dots from '../../../assets/icons/dots-vertical.svg';
import Chat from '../../../assets/icons/chat.svg';
import Image from '../../../assets/icons/image.svg';
import Heart from '../../../assets/icons/heart.svg';
import Send from '../../../assets/icons/send.svg';

import BText from "../../core/BText/BText";
import LText from "../../core/LText/LText";
import Button from "../../core/Button/Button";
import MessageBubble from "../MessageBubble/MessageBubble";
import { getMessageShape } from "../../../utils/utils";
import { dataRequested, getPersonChats } from "../../../widgets/messaging-overview-widget/model/effects";
import { useNavigate } from "react-router-dom";
import { setCurrentProfile } from "../../../widgets/profile-overview-widget/model/reducers";

const ChatOverview = () => {
    const currentConversation = useSelector(messageSelect.currentConversation);
    const dispatch = useDispatch();
    const jwtToken = useSelector(sessionSelect.jwtToken);
    const messages = useSelector(messageSelect.currentChat);
    const myUserId = useSelector(sessionSelect.userId);
    const [message, setMessage] = useState('');
    const [connected, setConnected] = useState(false);

    const connect = () => {
        const socket = new WebSocket(`ws://localhost:8083/chat/websocket?token=${jwtToken}`);
        const stompClient = Stomp.over(socket);
        stompClient.debug = console.log;

        stompClient.connect({}, () => {
            setConnected(true);
            console.log('Connected: ' + stompClient.connected);

            // Subscribing to personal queues based on userId
            const personalQueue = `/user/queue/${myUserId}/${currentConversation.userId}`;
            stompClient.subscribe(personalQueue, message => {
                console.log("Received personal message", message.body);
                dispatch(updateMessages());
            });
        }, (error: any) => {
            console.error('Connection error:', error);
        });

        return stompClient;
    };

    const disconnect = (stompClient: CompatClient) => {
        if (connected) {
            stompClient.disconnect(() => {
                console.log("Disconnected");
                setConnected(false);
            });
        }
    };

    const sendMessage = (stompClient: CompatClient) => {
        if (connected) {
            const messagePayload = {
                senderId: myUserId,
                receiverId: currentConversation.userId,
                content: message,
                timestamp: new Date().toISOString()
            };
            stompClient.send("/app/sendMessage", {}, JSON.stringify(messagePayload));
            setMessage('');
            dispatch(updateMessages());
        }
    };

    useEffect(() => {
        const stompClient = connect();
        return () => {
            disconnect(stompClient);
        };
    }, [currentConversation.userId]);

    const onMessageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setMessage(event.target.value);
    };

    return (
        <div className="chat-container">
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.senderId === myUserId ? 'my-message' : 'their-message'}`}>
                        {msg.content}
                    </div>
                ))}
            </div>
            <div className="message-input">
                <input
                    type="text"
                    value={message}
                    onChange={onMessageChange}
                    placeholder="Type a message..."
                />
                <button onClick={() => sendMessage} disabled={!message}>Send</button>
            </div>
        </div>
    );
};

export default ChatOverview;
