import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Button } from "react-bootstrap";
import Line from "../../components/core/Line/Line";
import { createPost } from "./model/effects";
import { sessionSelect } from "../../redux/core/session/selectors";
import './PostCreateWidget.css';
import {UploadButton} from "../../utils/uploadthing";
import "@uploadthing/react/styles.css";
import {OurFileRouter} from "../../app/api/uploadthing/core";

const PostCreateWidget: React.FC = () => {
    const userId = useSelector(sessionSelect.userId);
    const [description, setDescription] = useState<string>('');
    const [file, setFile] = useState<File | null>(null);

    const datePosted = new Date().toISOString().split('T')[0];
    const dispatch = useDispatch();

    const handleDescriptionChange = (event: React.ChangeEvent<HTMLInputElement>) => setDescription(event.target.value);
    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFile(event.target.files ? event.target.files[0] : null);
    };

    const uploadFile = async (file: File) => {
        const formData = new FormData();
        formData.append("file", file);

        const response = await fetch("https://api.uploadthing.com/upload", {
            method: "POST",
            headers: {
                "Authorization": "Bearer sk_live_031e6fada3707a21afcd57f8ae2d4a7e02f273eff16721784c2c3e52cc522564",
                "X-API-Key": "f8s67x9u8k"
            },
            body: formData
        });

        if (!response.ok) {
            throw new Error(`Failed to upload file: ${response.status} ${response.statusText}`);
        }

        return await response.json();
    };


    const handleSubmit = async () => {
        if (file) {
            try {
                const uploadResult = await uploadFile(file);
                if (uploadResult && uploadResult.url) {
                    await createPost({
                        userId, photo: uploadResult.url, datePosted, description, dispatch
                    });
                }
            } catch (error) {
                console.error('Upload Error:', error);
            }
        }
    };

    return (
        // <div className='widget-main-create'>
        //     <div>
        //         <div>
        //             <input
        //                 placeholder="Description"
        //                 type='text'
        //                 onChange={handleDescriptionChange}/>
        //             <input
        //                 type="file"
        //                 onChange={handleFileChange}/>
        //         </div>
        //         <Button onClick={handleSubmit}>Create Post</Button>
        //         <Line/>
        //     </div>
        // </div>
        <div className='widget-main-create'>
            <UploadButton
                endpoint="imageUploader"
                onClientUploadComplete={(res) => {
                    // Do something with the response
                    console.log("Files: ", res);
                    alert("Upload Completed");
                }}
                onUploadError={(error: Error) => {
                    // Do something with the error.
                    alert(`ERROR! ${error.message}`);
                }}
                onBeforeUploadBegin={(files) => {
                    // Preprocess files before uploading (e.g. rename them)
                    return files.map(
                        (f) => new File([f], "renamed-" + f.name, { type: f.type }),
                    );
                }}
                onUploadBegin={(name) => {
                    // Do something once upload begins
                    console.log("Uploading: ", name);
                }}
            />
        </div>
    )
}

export default PostCreateWidget;
